class InfoRepository extends EntityRepository
{

	public function VoirTout()
	{
		$requete = $this->createQueryBuilder('i')
					->getQuery()
					->getResult();
		return $requete;
	}
	
	public function VoirUn($id)
	{
		$requete = $this->_em->createQueryBuilder()
					->select('i')
					->from('PnpcScopsBundle:Info','i')
					->where('i.id = :id')
					->setParameter('id',$id)
					->getQuery()
					->getResult();
					
		return $requete;				
	}
	
